function timer(){var e=new JsSimpleDateFormat("dd/MM/yyyy HH:mm");var t=e.parse($("#fecha").val());t=new Date(t.getTime()+6e4);$("#fecha").val(e.format(t))}function load(){makeCookie()}function makeCookie(){var e="USER_TIMEZONE";var t=$("#timezones").val();setCookie("USER_TIMEZONE",t,30)}function onChangeGMT(){var e=$("#timezones").val();var t=$("#userId").val();if(t!=null&&t!=""){systemHour.calculateUser(e,t)}else{systemHour.calculateNotUser(e)}var n="USER_TIMEZONE";var r=e;setCookie(n,r,30)}function showInformationIntoView(e){$("#fecha").val(e.systemHour);location.reload()}var systemHour={calculateUser:function(e,t){var n={gmt:e,userId:t};generic.post("systemHour/calculateHourUser",n,generic.showInformation)},calculateNotUser:function(e){var t={gmt:e};generic.post("systemHour/calculateHourNotUser",t,generic.showInformation)}}